import api from './api';

export const authService = {
  // Login user
  login: (credentials) => {
    console.log('ðŸ” authService.login: Making login API call');
    return api.post('/auth/login', credentials);
  },

  // Register new user
  signup: (userData) => {
    console.log('ðŸ“ authService.signup: Making signup API call');
    console.log('ï¿½ï¿½ Signup data (authService):', {
      ...userData,
      password: '***',
      confirmPassword: '***'
    });
    return api.post('/auth/signup', userData);
  },

  // Change password (authenticated user) - with detailed logging
  changePassword: (passwordData) => {
    console.log('ðŸ” authService.changePassword: Making change password API call');
    console.log('ðŸ“¦ Change password request data structure:', {
      currentPassword: passwordData.currentPassword ? 'Present' : 'Missing',
      newPassword: passwordData.newPassword ? 'Present' : 'Missing',
      confirmPassword: passwordData.confirmPassword ? 'Present' : 'Missing',
      hasCurrentPassword: !!passwordData.currentPassword,
      hasNewPassword: !!passwordData.newPassword,
      passwordsMatch: passwordData.newPassword === passwordData.confirmPassword
    });

    // Send the data exactly as the backend expects
    return api.post('/auth/change-password', {
      currentPassword: passwordData.currentPassword,
      newPassword: passwordData.newPassword,
      confirmPassword: passwordData.confirmPassword
    });
  },

  // Alternative change password endpoint (some backends use different endpoints)
  changePasswordAlt: (passwordData) => {
    console.log('ðŸ” authService.changePasswordAlt: Trying alternative endpoint');
    return api.put('/auth/password', passwordData);
  },

  // Update password endpoint (another common variation)
  updatePassword: (passwordData) => {
    console.log('ðŸ” authService.updatePassword: Trying update endpoint');
    return api.patch('/auth/update-password', passwordData);
  },

  // Logout user
  logout: () => {
    console.log('ðŸšª authService.logout: Making logout API call');
    return api.post('/auth/logout');
  },

  // Set auth token in headers
  setAuthToken: (token) => {
    console.log('ðŸ”‘ authService.setAuthToken: Setting auth token in API headers');
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  },

  // Remove auth token from headers
  removeAuthToken: () => {
    console.log('ðŸ—‘ï¸ authService.removeAuthToken: Removing auth token from API headers');
    delete api.defaults.headers.common['Authorization'];
  },

  // Dashboard API calls
  getDashboardStats: () => {
    console.log('ðŸ“Š authService.getDashboardStats: Making dashboard stats API call');
    return api.get('/admin/dashboard/stats');
  },

  // Domain management
  getDomains: () => {
    console.log('ðŸ“ authService.getDomains: Making get domains API call');
    return api.get('/admin/domains');
  },

  // Project management
  getProjects: () => {
    console.log('ðŸ“‹ authService.getProjects: Making get projects API call');
    return api.get('/admin/projects');
  },

  // Leads management
  getLeads: () => {
    console.log('ðŸ‘¥ authService.getLeads: Making get leads API call');
    return api.get('/admin/leads');
  }
};
