// src/components/internships/ManageBranches.js

import React, { useState, useEffect } from 'react';
import { 
  FiPlus, 
  FiEdit, 
  FiTrash2, 
  FiSettings, 
  FiBookOpen,
  FiUsers,
  FiBarChart3,
  FiSearch,
  FiGrid,
  FiList
} from 'react-icons/fi';
import { toast } from 'react-toastify';
import Modal from '../common/Modal';
import ConfirmationModal from '../common/ConfirmationModal';
import { branchService } from '../../services/branchService';
import './ManageBranches.css';

const ManageBranches = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingBranch, setEditingBranch] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [branchToDelete, setBranchToDelete] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('grid');
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [showDomainModal, setShowDomainModal] = useState(false);
  const [showInternshipModal, setShowInternshipModal] = useState(false);

  useEffect(() => {
    fetchBranches();
  }, []);

  const fetchBranches = async () => {
    try {
      setLoading(true);
      const response = await branchService.getAllBranches();
      setBranches(response.data || []);
    } catch (error) {
      console.error('Error fetching branches:', error);
      toast.error('Failed to fetch branches');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveBranch = async (branchData) => {
    try {
      if (editingBranch) {
        await branchService.updateBranch(editingBranch.id, branchData);
        toast.success('Branch updated successfully');
      } else {
        await branchService.createBranch(branchData);
        toast.success('Branch created successfully');
      }

      setShowModal(false);
      setEditingBranch(null);
      fetchBranches();
    } catch (error) {
      console.error('Error saving branch:', error);
      toast.error('Failed to save branch');
    }
  };

  const handleDeleteBranch = async () => {
    try {
      await branchService.deleteBranch(branchToDelete.id);
      toast.success('Branch deleted successfully');
      setShowDeleteModal(false);
      setBranchToDelete(null);
      fetchBranches();
    } catch (error) {
      console.error('Error deleting branch:', error);
      toast.error('Failed to delete branch');
    }
  };

  const filteredBranches = branches.filter(branch =>
    branch.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    branch.code.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading branches...</p>
      </div>
    );
  }

  return (
    <div className="manage-branches">
      <div className="page-header">
        <div className="header-content">
          <h1>Manage Branches</h1>
          <p>Manage academic branches and their associated domains</p>
        </div>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setEditingBranch(null);
            setShowModal(true);
          }}
        >
          <FiPlus /> Add Branch
        </button>
      </div>

      <div className="page-controls">
        <div className="search-controls">
          <div className="search-box">
            <FiSearch />
            <input
              type="text"
              placeholder="Search branches..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        <div className="view-controls">
          <button
            className={`view-btn ${viewMode === 'grid' ? 'active' : ''}`}
            onClick={() => setViewMode('grid')}
          >
            <FiGrid />
          </button>
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => setViewMode('list')}
          >
            <FiList />
          </button>
        </div>
      </div>

      <div className={`branches-container ${viewMode}`}>
        {filteredBranches.map(branch => (
          <BranchCard
            key={branch.id}
            branch={branch}
            viewMode={viewMode}
            onEdit={(branch) => {
              setEditingBranch(branch);
              setShowModal(true);
            }}
            onDelete={(branch) => {
              setBranchToDelete(branch);
              setShowDeleteModal(true);
            }}
            onManageDomains={(branch) => {
              setSelectedBranch(branch);
              setShowDomainModal(true);
            }}
            onManageInternships={(branch) => {
              setSelectedBranch(branch);
              setShowInternshipModal(true);
            }}
          />
        ))}
      </div>

      {filteredBranches.length === 0 && !loading && (
        <div className="empty-state">
          <FiBookOpen size={48} />
          <h3>No branches found</h3>
          <p>Get started by creating your first branch</p>
          <button 
            className="btn btn-primary"
            onClick={() => {
              setEditingBranch(null);
              setShowModal(true);
            }}
          >
            <FiPlus /> Add Branch
          </button>
        </div>
      )}

      {/* Branch Modal */}
      {showModal && (
        <BranchModal
          branch={editingBranch}
          onClose={() => {
            setShowModal(false);
            setEditingBranch(null);
          }}
          onSave={handleSaveBranch}
        />
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <ConfirmationModal
          title="Delete Branch"
          message={`Are you sure you want to delete "${branchToDelete?.name}"? This action cannot be undone.`}
          onConfirm={handleDeleteBranch}
          onCancel={() => {
            setShowDeleteModal(false);
