// Branch Modal Component
const BranchModal = ({ branch, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    name: branch?.name || '',
    code: branch?.code || '',
    description: branch?.description || '',
    isActive: branch?.isActive !== undefined ? branch.isActive : true,
    sortOrder: branch?.sortOrder || 0
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const validate = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Branch name is required';
    } else if (formData.name.length < 2) {
      newErrors.name = 'Branch name must be at least 2 characters';
    }

    if (!formData.code.trim()) {
      newErrors.code = 'Branch code is required';
    } else if (formData.code.length < 2) {
      newErrors.code = 'Branch code must be at least 2 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validate()) return;

    setLoading(true);
    try {
      await onSave(formData);
    } catch (error) {
      console.error('Error saving branch:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Modal title={branch ? 'Edit Branch' : 'Add Branch'} onClose={onClose}>
      <form onSubmit={handleSubmit} className="branch-form">
        <div className="form-group">
          <label>Branch Name *</label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            placeholder="e.g., Computer Science & Engineering"
            className={errors.name ? 'error' : ''}
          />
          {errors.name && <span className="error-text">{errors.name}</span>}
        </div>

        <div className="form-group">
          <label>Branch Code *</label>
          <input
            type="text"
            value={formData.code}
            onChange={(e) => setFormData({ ...formData, code: e.target.value.toUpperCase() })}
            placeholder="e.g., CSE"
            className={errors.code ? 'error' : ''}
          />
          {errors.code && <span className="error-text">{errors.code}</span>}
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            placeholder="Brief description of the branch"
            rows={3}
          />
        </div>

        <div className="form-group">
          <label>Sort Order</label>
          <input
            type="number"
            value={formData.sortOrder}
            onChange={(e) => setFormData({ ...formData, sortOrder: parseInt(e.target.value) || 0 })}
            min="0"
          />
        </div>

        <div className="form-group">
          <label className="checkbox-label">
            <input
              type="checkbox"
              checked={formData.isActive}
              onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
            />
            Active
          </label>
        </div>

        <div className="form-actions">
          <button type="button" className="btn btn-secondary" onClick={onClose}>
            Cancel
          </button>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Saving...' : (branch ? 'Update' : 'Create')}
          </button>
        </div>
      </form>
    </Modal>
  );
};

// Domain Management Modal Component
const DomainManagementModal = ({ branch, onClose }) => {
  const [domains, setDomains] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showDomainModal, setShowDomainModal] = useState(false);
  const [editingDomain, setEditingDomain] = useState(null);

  useEffect(() => {
    fetchDomains();
  }, [branch.id]);

  const fetchDomains = async () => {
    try {
      setLoading(true);
      const response = await branchService.getDomainsByBranch(branch.id);
      setDomains(response.data || []);
    } catch (error) {
      console.error('Error fetching domains:', error);
      toast.error('Failed to fetch domains');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveDomain = async (domainData) => {
    try {
      const dataWithBranch = { ...domainData, branchId: branch.id };

      if (editingDomain) {
        await branchService.updateDomain(editingDomain.id, dataWithBranch);
        toast.success('Domain updated successfully');
      } else {
        await branchService.createDomain(dataWithBranch);
        toast.success('Domain created successfully');
      }

      setShowDomainModal(false);
      setEditingDomain(null);
      fetchDomains();
    } catch (error) {
      console.error('Error saving domain:', error);
      toast.error('Failed to save domain');
    }
  };

  const handleDeleteDomain = async (domain) => {
    if (window.confirm(`Are you sure you want to delete "${domain.name}"?`)) {
      try {
        await branchService.deleteDomain(domain.id);
        toast.success('Domain deleted successfully');
        fetchDomains();
      } catch (error) {
        console.error('Error deleting domain:', error);
        toast.error('Failed to delete domain');
      }
    }
  };

  return (
    <Modal 
      title={`Manage Domains - ${branch.name}`} 
      onClose={onClose}
      size="large"
    >
      <div className="domain-management">
        <div className="section-header">
          <h3>Domains under {branch.name}</h3>
          <button 
            className="btn btn-primary"
            onClick={() => {
              setEditingDomain(null);
              setShowDomainModal(true);
            }}
          >
            <FiPlus /> Add Domain
          </button>
        </div>

        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading domains...</p>
          </div>
        ) : (
          <div className="domains-grid">
            {domains.map(domain => (
              <div key={domain.id} className="domain-card">
                <div className="domain-header">
                  <h4>{domain.name}</h4>
                  <div className="domain-actions">
                    <button 
                      className="btn btn-sm btn-outline"
                      onClick={() => {
                        setEditingDomain(domain);
                        setShowDomainModal(true);
                      }}
                    >
                      <FiEdit />
                    </button>
                    <button 
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDeleteDomain(domain)}
                    >
                      <FiTrash2 />
                    </button>
                  </div>
                </div>

                {domain.description && (
                  <p className="domain-description">{domain.description}</p>
                )}

                <div className="domain-stats">
                  <span className="stat">
                    {domain.internshipCount || 0} Internships
                  </span>
                  <span className={`status ${domain.isActive ? 'active' : 'inactive'}`}>
                    {domain.isActive ? 'Active' : 'Inactive'}
                  </span>
                </div>
              </div>
            ))}

            {domains.length === 0 && (
              <div className="empty-state">
                <FiGrid size={32} />
                <p>No domains found</p>
                <button 
                  className="btn btn-primary"
                  onClick={() => {
                    setEditingDomain(null);
                    setShowDomainModal(true);
                  }}
                >
                  <FiPlus /> Add First Domain
                </button>
              </div>
            )}
          </div>
        )}

        {/* Domain Modal */}
        {showDomainModal && (
          <DomainModal
            domain={editingDomain}
            branch={branch}
            onClose={() => {
              setShowDomainModal(false);
              setEditingDomain(null);
            }}
            onSave={handleSaveDomain}
          />
        )}
      </div>
    </Modal>
  );
};

