    title: internship?.title || '',
    description: internship?.description || '',
    startDate: internship?.startDate ? internship.startDate.split('T')[0] : '',
    endDate: internship?.endDate ? internship.endDate.split('T')[0] : '',
    learningOutcomes: internship?.learningOutcomes || '',
    topBenefits: internship?.topBenefits || '',
    realTimeProjects: internship?.realTimeProjects || '',
    maxLearners: internship?.maxLearners || '',
    isFeatured: internship?.isFeatured || false,
    isActive: internship?.isActive !== undefined ? internship.isActive : true,
