            Cancel
          </button>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Saving...' : (internship ? 'Update' : 'Create')}
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default ManageBranches;
