// src/services/authService.js - REPLACE EXISTING
import axios from 'axios';
import { cookieUtils } from '../utils/cookieUtils';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';

class AuthService {
  constructor() {
    this.api = axios.create({
      baseURL: API_BASE_URL,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    // Request interceptor to add token
    this.api.interceptors.request.use(
      (config) => {
        const token = cookieUtils.getToken();
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => Promise.reject(error)
    );

    // Response interceptor for error handling
    this.api.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          this.logout();
          window.location.href = '/login';
        }
        return Promise.reject(error);
      }
    );
  }

  setAuthToken(token) {
    if (token) {
      this.api.defaults.headers.Authorization = `Bearer ${token}`;
    } else {
      delete this.api.defaults.headers.Authorization;
    }
  }

  removeAuthToken() {
    delete this.api.defaults.headers.Authorization;
  }

  // Auth methods
  async login(credentials) {
    const response = await this.api.post('/auth/login', credentials);
    return response.data;
  }

  async signup(userData) {
    const response = await this.api.post('/auth/signup', userData);
    return response.data;
  }

  async logout() {
    try {
      await this.api.post('/auth/logout');
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      cookieUtils.clearAuth();
      this.removeAuthToken();
    }
  }

  async getProfile() {
    const response = await this.api.get('/auth/profile');
    return response.data;
  }

  async changePassword(data) {
    const response = await this.api.post('/auth/change-password', data);
    return response.data;
  }

  async forgotPassword(email) {
    const response = await this.api.post('/auth/forgot-password', { email });
    return response.data;
  }

  async resetPassword(data) {
    const response = await this.api.post('/auth/reset-password', data);
    return response.data;
  }

  async verifyPasswordChangeOTP(data) {
    const response = await this.api.post('/auth/verify-password-change', data);
    return response.data;
  }

  // Dashboard APIs
  async getDashboardStats() {
    const response = await this.api.get('/admin/dashboard/stats');
    return response.data;
  }

  async getRecentActivity() {
    const response = await this.api.get('/admin/dashboard/recent-activity');
    return response.data;
  }

  async getAnalytics(period = 30) {
    const response = await this.api.get(`/admin/dashboard/analytics?period=${period}`);
    return response.data;
  }

  // Domain APIs
  async getDomains(params = {}) {
    const queryString = new URLSearchParams(params).toString();
    const response = await this.api.get(`/admin/domains?${queryString}`);
    return response.data;
  }

  async createDomain(data) {
    const response = await this.api.post('/admin/domains', data);
    return response.data;
  }

  async updateDomain(id, data) {
    const response = await this.api.put(`/admin/domains/${id}`, data);
    return response.data;
  }

  async deleteDomain(id) {
    const response = await this.api.delete(`/admin/domains/${id}`);
    return response.data;
  }

  async getDomainHierarchy(id) {
    const response = await this.api.get(`/admin/domains/${id}/hierarchy`);
    return response.data;
  }

  // SubDomain APIs
  async getSubDomains(params = {}) {
    const queryString = new URLSearchParams(params).toString();
    const response = await this.api.get(`/admin/subdomains?${queryString}`);
    return response.data;
  }

  async createSubDomain(data) {
    const response = await this.api.post('/admin/subdomains', data);
    return response.data;
  }

  async updateSubDomain(id, data) {
    const response = await this.api.put(`/admin/subdomains/${id}`, data);
    return response.data;
  }

  async deleteSubDomain(id) {
    const response = await this.api.delete(`/admin/subdomains/${id}`);
    return response.data;
  }

  async getLeafSubDomains(domainId) {
    const response = await this.api.get(`/admin/subdomains/leafs?domainId=${domainId}`);
    return response.data;
  }

  // Project APIs
  async getProjects(params = {}) {
    const queryString = new URLSearchParams(params).toString();
    const response = await this.api.get(`/admin/projects?${queryString}`);
    return response.data;
  }

  async createProject(data) {
    const response = await this.api.post('/admin/projects', data);
    return response.data;
  }

  async updateProject(id, data) {
    const response = await this.api.put(`/admin/projects/${id}`, data);
    return response.data;
  }

  async deleteProject(id) {
    const response = await this.api.delete(`/admin/projects/${id}`);
    return response.data;
  }

  async moveProject(id, data) {
    const response = await this.api.put(`/admin/projects/${id}/move`, data);
    return response.data;
  }

  async archiveProject(id, data) {
    const response = await this.api.put(`/admin/projects/${id}/archive`, data);
    return response.data;
  }

  // Lead APIs
  async getLeads(params = {}) {
    const queryString = new URLSearchParams(params).toString();
    const response = await this.api.get(`/admin/leads?${queryString}`);
    return response.data;
  }

  async updateLead(id, data) {
    const response = await this.api.put(`/admin/leads/${id}`, data);
    return response.data;
  }

  async exportLeads(params = {}) {
    const queryString = new URLSearchParams(params).toString();
    const response = await this.api.get(`/admin/leads/export/csv?${queryString}`, {
      responseType: 'blob'
    });
    return response.data;
  }

  // Image APIs
  async uploadImages(formData) {
    const response = await this.api.post('/admin/images/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  }

  async updateImage(id, data) {
    const response = await this.api.put(`/admin/images/${id}`, data);
    return response.data;
  }

  async deleteImage(id) {
    const response = await this.api.delete(`/admin/images/${id}`);
    return response.data;
  }

  async reorderImages(data) {
    const response = await this.api.put('/admin/images/reorder', data);
    return response.data;
  }
}

