// src/App.js - REPLACE EXISTING
import React from 'react';
import InternshipManagement from './components/internships/InternshipManagement';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/common/ProtectedRoute';
import AuthGuard from './components/common/AuthGuard';
import ComingSoon from './components/common/ComingSoon';
import Layout from './components/common/Layout';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';
import ForgotPassword from './components/auth/ForgotPassword';
import ResetPassword from './components/auth/ResetPassword';
import ChangePassword from './components/auth/ChangePassword';
import Dashboard from './components/dashboard/Dashboard';
import DomainManagement from './components/domains/DomainManagement';
import SubDomainManagement from './components/domains/SubDomainManagement';
import ProjectManagement from './components/projects/ProjectManagement';
import LeadManagement from './components/leads/LeadManagement';
import DebugAuth from './components/common/DebugAuth';

import 'react-toastify/dist/ReactToastify.css';
import './styles/global.css';

function App() {
  console.log('ðŸš€ App: Application starting');

  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Routes>
            {/* Public Routes - Protected from authenticated users */}
            <Route path="/login" element={
              <AuthGuard>
                <Login />
              </AuthGuard>
            } />
            <Route path="/signup" element={
              <AuthGuard>
                <Signup />
              </AuthGuard>
            } />
            <Route path="/forgot-password" element={
              <AuthGuard>
                <ForgotPassword />
              </AuthGuard>
            } />
            <Route path="/reset-password" element={
              <AuthGuard>
                <ResetPassword />
              </AuthGuard>
            } />

            <Route path="internships" element={<InternshipManagement />} />


            {/* Protected Routes - Require authentication */}
            <Route path="/" element={
              <ProtectedRoute>
                <Layout />
              </ProtectedRoute>
            }>
              {/* Redirect root to dashboard */}
              <Route index element={<Navigate to="/dashboard" replace />} />

              {/* Dashboard */}
              <Route path="dashboard" element={<Dashboard />} />

              {/* Account Management */}
              <Route path="change-password" element={<ChangePassword />} />

              {/* Domain Management */}
              <Route path="domains" element={<DomainManagement />} />
              <Route path="domains/:domainId/subdomains" element={<SubDomainManagement />} />

              {/* Project Management */}
              <Route path="projects" element={<ProjectManagement />} />

              {/* Lead Management */}
              <Route path="leads" element={<LeadManagement />} />

              {/* Future routes */}
              <Route path="settings" element={<ComingSoon title="Settings" />} />
              <Route path="analytics" element={<ComingSoon title="Analytics" />} />
              <Route path="reports" element={<ComingSoon title="Reports" />} />
            </Route>

            {/* Debug route - only in development */}
            {process.env.NODE_ENV === 'development' && (
              <Route path="/debug-auth" element={<DebugAuth />} />
            )}

            {/* Catch all route - redirect to dashboard if authenticated, login if not */}
            <Route path="*" element={<Navigate to="/login" replace />} />
          </Routes>

          <ToastContainer
            position="top-right"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            theme="light"
          />
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;


