const DomainModal = ({ domain, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    title: domain?.title || '',
    description: domain?.description || ''
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
