import api from './api';

export const authService = {
  // Login user
  login: (credentials) => {
    console.log('🔐 authService.login: Making login API call');
    return api.post('/auth/login', credentials);
  },

  // Register new user
  signup: (userData) => {
    console.log('📝 authService.signup: Making signup API call');
    return api.post('/auth/signup', userData);
  },

  // Forgot password - send reset email
  forgotPassword: (email) => {
    console.log('🔑 authService.forgotPassword: Making forgot password API call');
    return api.post('/auth/forgot-password', { email });
  },

  // Reset password with token
  resetPassword: (resetData) => {
    console.log('🔄 authService.resetPassword: Making reset password API call');
    return api.post('/auth/reset-password', resetData);
  },

  // Change password (authenticated user)
  changePassword: (passwordData) => {
    console.log('🔐 authService.changePassword: Making change password API call');
    return api.post('/auth/change-password', passwordData);
  },

  // Logout user
  logout: () => {
    console.log('🚪 authService.logout: Making logout API call');
    return api.post('/auth/logout');
  },

  // Verify email token
  verifyEmail: (token) => {
    console.log('✅ authService.verifyEmail: Making email verification API call');
    return api.post('/auth/verify-email', { token });
  },

  // Resend verification email
  resendVerification: (email) => {
    console.log('📧 authService.resendVerification: Making resend verification API call');
    return api.post('/auth/resend-verification', { email });
  },

  // Set auth token in headers
  setAuthToken: (token) => {
    console.log('🔑 authService.setAuthToken: Setting auth token in API headers');
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  },

  // Remove auth token from headers
  removeAuthToken: () => {
    console.log('🗑️ authService.removeAuthToken: Removing auth token from API headers');
    delete api.defaults.headers.common['Authorization'];
  },

  // Dashboard API calls
  getDashboardStats: () => {
    console.log('📊 authService.getDashboardStats: Making dashboard stats API call');
    return api.get('/admin/dashboard/stats');
  },

  // Domain management
  getDomains: () => {
    console.log('📁 authService.getDomains: Making get domains API call');
    return api.get('/admin/domains');
  },

  createDomain: (domainData) => {
    console.log('➕ authService.createDomain: Making create domain API call');
    return api.post('/admin/domains', domainData);
  },

  updateDomain: (id, domainData) => {
    console.log('✏️ authService.updateDomain: Making update domain API call');
    return api.put(`/admin/domains/${id}`, domainData);
  },

  deleteDomain: (id) => {
    console.log('🗑️ authService.deleteDomain: Making delete domain API call');
    return api.delete(`/admin/domains/${id}`);
  },

  // Project management
  getProjects: () => {
    console.log('📋 authService.getProjects: Making get projects API call');
    return api.get('/admin/projects');
  },

  createProject: (projectData) => {
    console.log('➕ authService.createProject: Making create project API call');
    return api.post('/admin/projects', projectData);
  },

  updateProject: (id, projectData) => {
    console.log('✏️ authService.updateProject: Making update project API call');
    return api.put(`/admin/projects/${id}`, projectData);
  },

  deleteProject: (id) => {
    console.log('🗑️ authService.deleteProject: Making delete project API call');
    return api.delete(`/admin/projects/${id}`);
  },

  // Leads management
  getLeads: () => {
    console.log('👥 authService.getLeads: Making get leads API call');
    return api.get('/admin/leads');
  },

  updateLead: (id, leadData) => {
    console.log('✏️ authService.updateLead: Making update lead API call');
    return api.put(`/admin/leads/${id}`, leadData);
  },

  deleteLead: (id) => {
    console.log('🗑️ authService.deleteLead: Making delete lead API call');
    return api.delete(`/admin/leads/${id}`);
  },

  // Export leads to CSV
  exportLeads: () => {
    console.log('📄 authService.exportLeads: Making export leads API call');
    return api.get('/admin/leads/export', { responseType: 'blob' });
  }
};
