// src/components/internships/InternshipManagement.js - Complete File
import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { 
  FiPlus, 
  FiEdit, 
  FiTrash2, 
  FiEye,
  FiUsers,
  FiStar,
  FiCalendar,
  FiDollarSign,
  FiSearch,
  FiFilter,
  FiBookOpen
} from 'react-icons/fi';
import { internshipService } from '../../services/internshipService';
import LoadingSpinner from '../common/LoadingSpinner';
import Modal from '../common/Modal';
import './InternshipManagement.css';

const InternshipManagement = () => {
  const [internships, setInternships] = useState([]);
  const [branches, setBranches] = useState([]);
  const [domains, setDomains] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedInternship, setSelectedInternship] = useState(null);
  const [formLoading, setFormLoading] = useState(false);
  const [selectedBranchForForm, setSelectedBranchForForm] = useState('');
  const [filters, setFilters] = useState({
    search: '',
    branchId: '',
    domainId: '',
    isActive: '',
    isFeatured: ''
  });
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    shortDescription: '',
    learningOutcomes: '',
    topBenefits: '',
    realTimeProjects: '',
    startDate: '',
    endDate: '',
    price: '',
    originalPrice: '',
    maxLearners: '',
    prerequisites: '',
    internshipDomainId: '',
    isActive: true,
    isFeatured: false,
    isComingSoon: false
  });

  useEffect(() => {
    fetchInitialData();
  }, []);

  useEffect(() => {
    fetchInternships();
  }, [filters]);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      const [internshipsRes, branchesRes] = await Promise.all([
        internshipService.getInternships(),
        internshipService.getBranches()
      ]);

      setInternships(internshipsRes.data.internships || []);
      setBranches(branchesRes.data.branches || []);
    } catch (error) {
      console.error('Error fetching initial data:', error);
      toast.error('Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  const fetchInternships = async () => {
    try {
      const response = await internshipService.getInternships(filters);
      setInternships(response.data.internships || []);
    } catch (error) {
      console.error('Error fetching internships:', error);
      toast.error('Failed to fetch internships');
    }
  };

  const fetchDomainsByBranch = async (branchId) => {
    if (!branchId) {
      setDomains([]);
      return;
    }

    try {
      const response = await internshipService.getDomainsByBranch(branchId);
      setDomains(response.data || []);
    } catch (error) {
      console.error('Error fetching domains:', error);
      toast.error('Failed to fetch domains');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormLoading(true);

    try {
      if (selectedInternship) {
        await internshipService.updateInternship(selectedInternship.id, formData);
        toast.success('Internship updated successfully');
        setShowEditModal(false);
      } else {
        await internshipService.createInternship(formData);
        toast.success('Internship created successfully');
        setShowAddModal(false);
      }

      resetForm();
      fetchInternships();
    } catch (error) {
      console.error('Error saving internship:', error);
      toast.error(error.response?.data?.message || 'Failed to save internship');
    } finally {
      setFormLoading(false);
    }
  };

  const handleEdit = async (internship) => {
    setSelectedInternship(internship);

    // Set the branch for the form
    const branchId = internship.internshipDomain?.branchId;
    setSelectedBranchForForm(branchId);

    // Fetch domains for the internship's branch
    if (branchId) {
      await fetchDomainsByBranch(branchId);
    }

    setFormData({
      title: internship.title,
      description: internship.description,
      shortDescription: internship.shortDescription || '',
      learningOutcomes: internship.learningOutcomes,
      topBenefits: internship.topBenefits || '',
      realTimeProjects: internship.realTimeProjects || '',
      startDate: internship.startDate ? new Date(internship.startDate).toISOString().split('T')[0] : '',
      endDate: internship.endDate ? new Date(internship.endDate).toISOString().split('T')[0] : '',
      price: internship.price || '',
      originalPrice: internship.originalPrice || '',
      maxLearners: internship.maxLearners || '',
      prerequisites: internship.prerequisites || '',
      internshipDomainId: internship.internshipDomainId,
      isActive: internship.isActive,
      isFeatured: internship.isFeatured,
      isComingSoon: internship.isComingSoon
    });
    setShowEditModal(true);
  };

  const handleDelete = async (internship) => {
    if (window.confirm(`Are you sure you want to delete "${internship.title}"?`)) {
      try {
        await internshipService.deleteInternship(internship.id);
        toast.success('Internship deleted successfully');
        fetchInternships();
      } catch (error) {
        console.error('Error deleting internship:', error);
        toast.error(error.response?.data?.message || 'Failed to delete internship');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      shortDescription: '',
      learningOutcomes: '',
      topBenefits: '',
      realTimeProjects: '',
      startDate: '',
      endDate: '',
      price: '',
      originalPrice: '',
      maxLearners: '',
      prerequisites: '',
      internshipDomainId: '',
      isActive: true,
      isFeatured: false,
      isComingSoon: false
    });
    setSelectedInternship(null);
    setSelectedBranchForForm('');
    setDomains([]);
  };

  const handleCloseModal = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    resetForm();
  };

  const handleBranchChange = async (branchId) => {
    setSelectedBranchForForm(branchId);
    setFormData(prev => ({ 
      ...prev, 
      internshipDomainId: '' // Reset domain when branch changes
    }));
    await fetchDomainsByBranch(branchId);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  if (loading) {
    return <LoadingSpinner message="Loading internships..." />;
  }

  return (
    <div className="internship-management">
      {/* Header */}
      <div className="page-header">
        <div className="header-content">
          <h1>Internship Management</h1>
          <p>Manage internship programs across all branches and domains</p>
        </div>
        <div className="header-actions">
          <button 
            className="primary-button"
            onClick={() => setShowAddModal(true)}
          >
            <FiPlus />
            Add Internship
          </button>
        </div>
      </div>

      {/* Filters */}
      <div className="filters-section">
        <div className="filters-grid">
          <div className="filter-group">
            <FiSearch className="filter-icon" />
            <input
              type="text"
              placeholder="Search internships..."
              value={filters.search}
              onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
            />
          </div>

          <select
            value={filters.branchId}
            onChange={(e) => setFilters(prev => ({ ...prev, branchId: e.target.value, domainId: '' }))}
          >
            <option value="">All Branches</option>
            {branches.map(branch => (
              <option key={branch.id} value={branch.id}>{branch.name}</option>
            ))}
          </select>

          <select
            value={filters.isActive}
            onChange={(e) => setFilters(prev => ({ ...prev, isActive: e.target.value }))}
          >
            <option value="">All Status</option>
            <option value="true">Active</option>
            <option value="false">Inactive</option>
          </select>

          <select
            value={filters.isFeatured}
            onChange={(e) => setFilters(prev => ({ ...prev, isFeatured: e.target.value }))}
          >
            <option value="">All Types</option>
            <option value="true">Featured</option>
            <option value="false">Regular</option>
          </select>
        </div>
      </div>

      {/* Internships List */}
      <div className="internships-grid">
        {internships.map((internship) => (
          <div key={internship.id} className={`internship-card ${!internship.isActive ? 'inactive' : ''}`}>
            <div className="internship-header">
              <div className="internship-badges">
                {internship.isFeatured && <span className="badge featured">Featured</span>}
                {internship.isComingSoon && <span className="badge coming-soon">Coming Soon</span>}
                {!internship.isActive && <span className="badge inactive">Inactive</span>}
              </div>
              <h3 className="internship-title">{internship.title}</h3>
              <p className="internship-domain">
                {internship.internshipDomain?.branch?.name} • {internship.internshipDomain?.name}
              </p>
            </div>

            {internship.shortDescription && (
              <p className="internship-description">{internship.shortDescription}</p>
            )}

            <div className="internship-details">
              <div className="detail-item">
                <FiCalendar className="detail-icon" />
                <span>{formatDate(internship.startDate)} - {formatDate(internship.endDate)}</span>
              </div>

              {internship.price > 0 && (
                <div className="detail-item">
                  <FiDollarSign className="detail-icon" />
                  <span>
                    {formatCurrency(internship.price)}
                    {internship.originalPrice && internship.originalPrice > internship.price && (
                      <span className="original-price">{formatCurrency(internship.originalPrice)}</span>
                    )}
                  </span>
                </div>
              )}

              <div className="detail-item">
                <FiUsers className="detail-icon" />
                <span>{internship.currentLearners || 0} / {internship.maxLearners || '∞'} learners</span>
              </div>

              {internship.averageRating > 0 && (
                <div className="detail-item">
                  <FiStar className="detail-icon" />
                  <span>{internship.averageRating} ({internship.totalRatings} reviews)</span>
                </div>
              )}
            </div>

            <div className="internship-actions">
              <button
                className="secondary-button"
                onClick={() => handleEdit(internship)}
              >
                <FiEdit />
                Edit
              </button>
              <button
                className="delete-button"
                onClick={() => handleDelete(internship)}
              >
                <FiTrash2 />
              </button>
            </div>
          </div>
        ))}

        {internships.length === 0 && (
          <div className="empty-state">
            <FiBookOpen className="empty-icon" />
            <h3>No internships found</h3>
            <p>Create your first internship program to get started</p>
            <button 
              className="primary-button"
              onClick={() => setShowAddModal(true)}
            >
              <FiPlus />
              Add First Internship
            </button>
          </div>
        )}
      </div>

      {/* Add/Edit Internship Modal */}
      {(showAddModal || showEditModal) && (
        <Modal
          title={selectedInternship ? 'Edit Internship' : 'Add New Internship'}
          onClose={handleCloseModal}
          size="large"
        >
          <form onSubmit={handleSubmit} className="internship-form">
            {/* Basic Information */}
            <div className="form-section">
              <h4>Basic Information</h4>

              <div className="form-group">
                <label>Internship Title *</label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                  placeholder="e.g., AI/ML Engineer Internship - Complete Practical Training"
                  required
                />
              </div>

              <div className="form-grid">
                <div className="form-group">
                  <label>Branch *</label>
                  <select
                    value={selectedBranchForForm}
                    onChange={(e) => handleBranchChange(e.target.value)}
                    required
                    disabled={selectedInternship} // Don't allow changing branch in edit mode
                  >
                    <option value="">Select Branch</option>
                    {branches.map(branch => (
                      <option key={branch.id} value={branch.id}>{branch.name}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Domain *</label>
                  <select
                    value={formData.internshipDomainId}
                    onChange={(e) => setFormData(prev => ({ ...prev, internshipDomainId: e.target.value }))}
                    required
                  >
                    <option value="">Select Domain</option>
                    {domains.map(domain => (
                      <option key={domain.id} value={domain.id}>{domain.name}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Short Description</label>
                <input
                  type="text"
                  value={formData.shortDescription}
                  onChange={(e) => setFormData(prev => ({ ...prev, shortDescription: e.target.value }))}
                  placeholder="Brief description for listing pages"
                  maxLength={500}
                />
              </div>

              <div className="form-group">
                <label>Full Description *</label>
                <textarea
                  value={formData.description}
                  onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                  rows={4}
                  placeholder="Detailed description of the internship program..."
                  required
                />
              </div>
            </div>

            {/* Schedule & Pricing */}
            <div className="form-section">
              <h4>Schedule & Pricing</h4>

              <div className="form-grid">
                <div className="form-group">
                  <label>Start Date *</label>
                  <input
                    type="date"
                    value={formData.startDate}
                    onChange={(e) => setFormData(prev => ({ ...prev, startDate: e.target.value }))}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>End Date *</label>
                  <input
                    type="date"
                    value={formData.endDate}
                    onChange={(e) => setFormData(prev => ({ ...prev, endDate: e.target.value }))}
                    required
                  />
                </div>
              </div>

              <div className="form-grid">
                <div className="form-group">
                  <label>Price (₹)</label>
                  <input
                    type="number"
                    value={formData.price}
                    onChange={(e) => setFormData(prev => ({ ...prev, price: e.target.value }))}
                    placeholder="0"
                    min="0"
                    step="0.01"
                  />
                </div>

                <div className="form-group">
                  <label>Original Price (₹)</label>
                  <input
                    type="number"
                    value={formData.originalPrice}
                    onChange={(e) => setFormData(prev => ({ ...prev, originalPrice: e.target.value }))}
                    placeholder="For showing discounts"
                    min="0"
                    step="0.01"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Maximum Learners</label>
                <input
                  type="number"
                  value={formData.maxLearners}
                  onChange={(e) => setFormData(prev => ({ ...prev, maxLearners: e.target.value }))}
                  placeholder="Leave empty for unlimited"
                  min="1"
                />
              </div>
            </div>

            {/* Content Details */}
            <div className="form-section">
              <h4>Content Details</h4>

              <div className="form-group">
                <label>Learning Outcomes *</label>
                <textarea
                  value={formData.learningOutcomes}
                  onChange={(e) => setFormData(prev => ({ ...prev, learningOutcomes: e.target.value }))}
                  rows={3}
                  placeholder="What will students learn? (bullet points or paragraphs)"
                  required
                />
              </div>

              <div className="form-group">
                <label>Top Benefits</label>
                <textarea
                  value={formData.topBenefits}
                  onChange={(e) => setFormData(prev => ({ ...prev, topBenefits: e.target.value }))}
                  rows={3}
                  placeholder="Key benefits of this internship..."
                />
              </div>

              <div className="form-group">
                <label>Real-time Projects</label>
                <textarea
                  value={formData.realTimeProjects}
                  onChange={(e) => setFormData(prev => ({ ...prev, realTimeProjects: e.target.value }))}
                  rows={3}
                  placeholder="List of projects students will work on..."
                />
              </div>

              <div className="form-group">
                <label>Prerequisites</label>
                <textarea
                  value={formData.prerequisites}
                  onChange={(e) => setFormData(prev => ({ ...prev, prerequisites: e.target.value }))}
                  rows={2}
                  placeholder="Required skills or knowledge..."
                />
              </div>
            </div>

            {/* Settings */}
            <div className="form-section">
              <h4>Settings</h4>

              <div className="form-checkboxes">
                <label>
                  <input
                    type="checkbox"
                    checked={formData.isActive}
                    onChange={(e) => setFormData(prev => ({ ...prev, isActive: e.target.checked }))}
                  />
                  Active Internship
                </label>

                <label>
                  <input
                    type="checkbox"
                    checked={formData.isFeatured}
                    onChange={(e) => setFormData(prev => ({ ...prev, isFeatured: e.target.checked }))}
                  />
                  Featured Internship
                </label>

                <label>
                  <input
                    type="checkbox"
                    checked={formData.isComingSoon}
                    onChange={(e) => setFormData(prev => ({ ...prev, isComingSoon: e.target.checked }))}
                  />
                  Coming Soon
                </label>
              </div>
            </div>

            <div className="modal-actions">
              <button type="button" onClick={handleCloseModal} disabled={formLoading}>
                Cancel
              </button>
              <button type="submit" className="primary-button" disabled={formLoading}>
                {formLoading ? 'Saving...' : (selectedInternship ? 'Update Internship' : 'Create Internship')}
              </button>
            </div>
          </form>
        </Modal>
      )}
    </div>
  );
};

export default InternshipManagement;
