import React from 'react';
import { useAuth } from '../../context/AuthContext';
import { cookieUtils } from '../../utils/cookieUtils';

const DebugAuth = () => {
  const { isAuthenticated, user, loading } = useAuth();

  const handleCheckCookies = () => {
    const token = cookieUtils.getToken();
    const userData = cookieUtils.getUser();

    console.log('üîç Debug: Cookie check results:', {
      token: token ? 'Present' : 'Missing',
      userData: userData ? 'Present' : 'Missing',
      isAuthenticated,
      loading,
      user
    });

    alert(`Token: ${token ? 'Present' : 'Missing'}\nUser Data: ${userData ? 'Present' : 'Missing'}\nAuthenticated: ${isAuthenticated}`);
  };

  if (process.env.NODE_ENV === 'production') {
    return null;
  }

  return (
    <div style={{
      position: 'fixed',
      bottom: '20px',
      right: '20px',
      background: '#333',
      color: 'white',
      padding: '10px',
      borderRadius: '5px',
      zIndex: 1000,
      fontSize: '12px'
    }}>
      <button onClick={handleCheckCookies} style={{
        background: '#007bff',
        color: 'white',
        border: 'none',
        padding: '5px 10px',
        borderRadius: '3px',
        cursor: 'pointer'
      }}>
        Debug Auth
      </button>
      <div>Auth: {isAuthenticated ? '‚úÖ' : '‚ùå'}</div>
      <div>Loading: {loading ? '‚è≥' : '‚úÖ'}</div>
    </div>
  );
};

export default DebugAuth;
