// components/dashboard/Dashboard.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { authService } from '../../services/authService';
import { toast } from 'react-toastify';
import StatsCard from './StatsCard';
import { 
  FiFolder, 
  FiFileText, 
  FiUsers, 
  FiTrendingUp,
  FiCalendar,
  FiActivity
} from 'react-icons/fi';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalDomains: 0,
    totalProjects: 0,
    totalLeads: 0,
    recentLeads: 0,
    loading: true
  });
  const [recentActivity, setRecentActivity] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // In a real application, you would call the actual API
      // const response = await authService.getDashboardStats();

      // Mock data for demonstration
      setTimeout(() => {
        setStats({
          totalDomains: 12,
          totalProjects: 145,
          totalLeads: 89,
          recentLeads: 15,
          loading: false
        });

        setRecentActivity([
          {
            id: 1,
            type: 'lead',
            message: 'New lead from John Doe - React Development',
            time: '2 hours ago'
          },
          {
            id: 2,
            type: 'project',
            message: 'Project "E-commerce Platform" was updated',
            time: '4 hours ago'
          },
          {
            id: 3,
            type: 'domain',
            message: 'New domain "Mobile Apps" was added',
            time: '1 day ago'
          },
          {
            id: 4,
            type: 'lead',
            message: 'New lead from Sarah Smith - UI/UX Design',
            time: '1 day ago'
          }
        ]);
      }, 1000);
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
      setStats(prev => ({ ...prev, loading: false }));
    }
  };

  const statsData = [
    {
      title: 'Total Domains',
      value: stats.totalDomains,
      icon: FiFolder,
      color: 'blue',
      change: '+12%',
      changeType: 'positive'
    },
    {
      title: 'Total Projects',
      value: stats.totalProjects,
      icon: FiFileText,
      color: 'green',
      change: '+8%',
      changeType: 'positive'
    },
    {
      title: 'Total Leads',
      value: stats.totalLeads,
      icon: FiUsers,
      color: 'purple',
      change: '+25%',
      changeType: 'positive'
    },
    {
      title: 'Recent Leads',
      value: stats.recentLeads,
      icon: FiTrendingUp,
      color: 'orange',
      change: '+5%',
      changeType: 'positive'
    }
  ];

  const getActivityIcon = (type) => {
    switch (type) {
      case 'lead':
        return <FiUsers />;
      case 'project':
        return <FiFileText />;
      case 'domain':
        return <FiFolder />;
      default:
        return <FiActivity />;
    }
  };

  if (stats.loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div>
          <h1>Welcome back, {user?.name || 'Admin'}!</h1>
          <p>Here's what's happening with your projects today.</p>
        </div>
        <div className="dashboard-date">
          <FiCalendar />
          <span>{new Date().toLocaleDateString('en-US', { 
            weekday: 'long', 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
          })}</span>
        </div>
      </div>

      <div className="stats-grid">
        {statsData.map((stat, index) => (
          <StatsCard key={index} {...stat} />
        ))}
      </div>

      <div className="dashboard-content">
        <div className="dashboard-section">
          <div className="section-header">
            <h2>Recent Activity</h2>
            <button className="view-all-button">View All</button>
          </div>

          <div className="activity-list">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="activity-item">
                <div className="activity-icon">
                  {getActivityIcon(activity.type)}
                </div>
                <div className="activity-content">
                  <p className="activity-message">{activity.message}</p>
                  <span className="activity-time">{activity.time}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="dashboard-section">
          <div className="section-header">
            <h2>Quick Actions</h2>
          </div>

          <div className="quick-actions">
            <button className="action-button">
              <FiFolder />
              <span>Add Domain</span>
            </button>
            <button className="action-button">
              <FiFileText />
              <span>Add Project</span>
            </button>
            <button className="action-button">
              <FiUsers />
              <span>View Leads</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
