// src/components/common/Sidebar.js - Updated with Internship Menu
import React from 'react';
import { NavLink } from 'react-router-dom';
import { 
  FiHome, 
  FiFolder, 
  FiFileText, 
  FiUsers, 
  FiSettings,
  FiLock,
  FiBookOpen,
  FiGitBranch,
  FiAward,
  FiUserCheck,
  FiStar
} from 'react-icons/fi';

const Sidebar = ({ isOpen }) => {
  const menuItems = [
    {
      path: '/dashboard',
      icon: FiHome,
      label: 'Dashboard'
    },
    {
      section: 'Projects System',
      items: [
        {
          path: '/domains',
          icon: FiFolder,
          label: 'Domains'
        },
        {
          path: '/projects',
          icon: FiFileText,
          label: 'Projects'
        },
        {
          path: '/leads',
          icon: FiUsers,
          label: 'Project Leads'
        }
      ]
    },
    {
      section: 'Internships System',
      items: [
        {
          path: '/branches',
          icon: FiGitBranch,
          label: 'Branches'
        },
        {
          path: '/internships',
          icon: FiBookOpen,
          label: 'Internships'
        },
        {
          path: '/internship-leads',
          icon: FiUserCheck,
          label: 'Enrollments'
        },
        {
          path: '/ratings',
          icon: FiStar,
          label: 'Ratings & Reviews'
        }
      ]
    },
    {
      section: 'Account',
      items: [
        {
          path: '/change-password',
          icon: FiLock,
          label: 'Change Password'
        },
        {
          path: '/settings',
          icon: FiSettings,
          label: 'Settings'
        }
      ]
    }
  ];

  return (
    <aside className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
      <nav className="sidebar-nav">
        <ul className="nav-list">
          {/* Dashboard - standalone item */}
          <li key="dashboard" className="nav-item">
            <NavLink
              to="/dashboard"
              className={({ isActive }) => 
                `nav-link ${isActive ? 'active' : ''}`
              }
            >
              <FiHome className="nav-icon" />
              {isOpen && <span className="nav-label">Dashboard</span>}
            </NavLink>
          </li>

          {/* Grouped menu items */}
          {menuItems.slice(1).map((group, groupIndex) => (
            <React.Fragment key={groupIndex}>
              {isOpen && (
                <li className="nav-section">
                  <span className="nav-section-title">{group.section}</span>
                </li>
              )}
              {group.items.map((item) => {
                const Icon = item.icon;
                return (
                  <li key={item.path} className="nav-item">
                    <NavLink
                      to={item.path}
                      className={({ isActive }) => 
                        `nav-link ${isActive ? 'active' : ''}`
                      }
                    >
                      <Icon className="nav-icon" />
                      {isOpen && <span className="nav-label">{item.label}</span>}
                    </NavLink>
                  </li>
                );
              })}
            </React.Fragment>
          ))}
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
