// Domain Modal Component
const DomainModal = ({ domain, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    title: domain?.title || '',
    description: domain?.description || ''
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      console.log('ğŸ’¾ DOMAIN SAVE - Starting API call');
      console.log('ğŸ“‹ DOMAIN SAVE - Form data:', formData);
      console.log('ğŸ“‹ DOMAIN SAVE - Is editing?', !!domain);
      console.log('ğŸ“‹ DOMAIN SAVE - Domain ID (if editing):', domain?.id);

      if (domain) {
        console.log('ğŸŒ DOMAIN SAVE - API URL: /admin/domains/' + domain.id + ' (PUT)');
        const response = await authService.updateDomain(domain.id, formData);

        console.log('âœ… DOMAIN UPDATE - Full API Response:', response);
        console.log('ğŸ“¦ DOMAIN UPDATE - Response status:', response.status);
        console.log('ğŸ“¦ DOMAIN UPDATE - Response data:', response.data);

        toast.success('Domain updated successfully');
      } else {
        console.log('ğŸŒ DOMAIN SAVE - API URL: /admin/domains (POST)');
        const response = await authService.createDomain(formData);

        console.log('âœ… DOMAIN CREATE - Full API Response:', response);
        console.log('ğŸ“¦ DOMAIN CREATE - Response status:', response.status);
        console.log('ğŸ“¦ DOMAIN CREATE - Response data:', response.data);

        toast.success('Domain created successfully');
      }

      console.log('ğŸ¯ DOMAIN SAVE - Success, calling onSave callback');
      onSave();
    } catch (error) {
      console.error('âŒ DOMAIN SAVE - Error occurred:', error);
      console.error('âŒ DOMAIN SAVE - Error response:', error.response);
      console.error('âŒ DOMAIN SAVE - Error response data:', error.response?.data);
      console.error('âŒ DOMAIN SAVE - Error message:', error.message);
      toast.error('Failed to save domain');
    } finally {
      setLoading(false);
      console.log('ğŸ DOMAIN SAVE - Loading set to false');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>{domain ? 'Edit Domain' : 'Add New Domain'}</h2>
          <button onClick={onClose}>Ã—</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Domain Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              required
            />
          </div>

          <div className="form-group">
            <label>Description (Optional)</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              rows={3}
            />
          </div>

          <div className="modal-actions">
            <button type="button" onClick={onClose} disabled={loading}>
              Cancel
            </button>
            <button type="submit" className="primary-button" disabled={loading}>
              {loading ? 'Saving...' : 'Save'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};


// Hierarchy Modal Component
const HierarchyModal = ({ domain, onClose }) => {
  return (
    <div className="modal-overlay">
      <div className="modal large">
        <div className="modal-header">
          <h2>Domain Hierarchy: {domain.title}</h2>
          <button onClick={onClose}>Ã—</button>
        </div>

        <div className="hierarchy-content">
          <div className="hierarchy-tree">
            {domain.subDomains && domain.subDomains.length > 0 ? (
              domain.subDomains.map((subDomain) => (
                <HierarchyNode key={subDomain.id} node={subDomain} level={0} />
              ))
            ) : (
              <p>No sub-domains found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Hierarchy Node Component
const HierarchyNode = ({ node, level }) => {
  const [expanded, setExpanded] = useState(true);

  return (
    <div className={`hierarchy-node level-${level}`}>
      <div className="node-header">
        {node.children && node.children.length > 0 && (
          <button 
            className="expand-button"
            onClick={() => setExpanded(!expanded)}
          >
            <FiChevronRight className={expanded ? 'expanded' : ''} />
          </button>
        )}
        <FiFolder className="node-icon" />
        <span className="node-title">{node.title}</span>
        {node.projects && (
          <span className="project-count">({node.projects.length} projects)</span>
        )}
      </div>

      {expanded && node.children && node.children.length > 0 && (
        <div className="node-children">
          {node.children.map((child) => (
            <HierarchyNode key={child.id} node={child} level={level + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

export default DomainManagement;


