import Cookies from 'js-cookie';

const TOKEN_KEY = 'admin_token';
const USER_KEY = 'admin_user';
const COOKIE_OPTIONS = {
  expires: 7, // 7 days
  secure: process.env.NODE_ENV === 'production', // Only secure in production
  sameSite: 'lax',
  path: '/'
};

export const cookieUtils = {
  // Set authentication token
  setToken: (token) => {
    console.log('üç™ Setting token in cookies');
    Cookies.set(TOKEN_KEY, token, COOKIE_OPTIONS);
  },

  // Get authentication token
  getToken: () => {
    const token = Cookies.get(TOKEN_KEY);
    console.log('üç™ Getting token from cookies:', token ? 'Found' : 'Not found');
    return token;
  },

  // Remove authentication token
  removeToken: () => {
    console.log('üç™ Removing token from cookies');
    Cookies.remove(TOKEN_KEY, { path: '/' });
  },

  // Set user data
  setUser: (userData) => {
    console.log('üç™ Setting user data in cookies');
    Cookies.set(USER_KEY, JSON.stringify(userData), COOKIE_OPTIONS);
  },

  // Get user data
  getUser: () => {
    const userData = Cookies.get(USER_KEY);
    console.log('üç™ Getting user data from cookies:', userData ? 'Found' : 'Not found');
    if (userData) {
      try {
        return JSON.parse(userData);
      } catch (error) {
        console.error('üç™ Error parsing user data from cookies:', error);
        return null;
      }
    }
    return null;
  },

  // Remove user data
  removeUser: () => {
    console.log('üç™ Removing user data from cookies');
    Cookies.remove(USER_KEY, { path: '/' });
  },

  // Clear all auth data
  clearAuth: () => {
    console.log('üç™ Clearing all auth data from cookies');
    cookieUtils.removeToken();
    cookieUtils.removeUser();
  },

  // Check if user is authenticated
  isAuthenticated: () => {
    const token = cookieUtils.getToken();
    const user = cookieUtils.getUser();
    const isAuth = !!(token && user);
    console.log('üç™ Checking authentication status:', isAuth);
    return isAuth;
  }
};
